paramConversion.applyStepsToNormalizedParameterValue:
function applyStepsToNormalizedParameterValue(normalizedValue, steps) {
    if (steps == 1) {
        if (normalizedValue > 0) {
            normalizedValue = 1.;
        }
    } else {
        let oneStep = 1. / (steps - 1);
        let numberOfSteps = rnbo_fround(normalizedValue / oneStep * 1 / 1) * 1;
        normalizedValue = numberOfSteps * oneStep;
    }

    return normalizedValue;
}

paramConversion.convertToNormalizedParameterValue:
function convertToNormalizedParameterValue(index, value) {
    switch (index) {
    case 3:
        {
            value = (value < 1 ? 1 : (value > 50 ? 50 : value));
            let normalizedValue = (value - 1) / (50 - 1);
            return normalizedValue;
        }
    case 0:
        {
            value = (value < 1 ? 1 : (value > 500 ? 500 : value));
            let normalizedValue = (value - 1) / (500 - 1);
            return normalizedValue;
        }
    case 1:
        {
            value = (value < 10 ? 10 : (value > 500 ? 500 : value));
            let normalizedValue = (value - 10) / (500 - 10);
            return normalizedValue;
        }
    case 2:
        {
            value = (value < -12 ? -12 : (value > 12 ? 12 : value));
            let normalizedValue = (value - -12) / (12 - -12);
            return normalizedValue;
        }
    default:
        return value;
    }
}

paramConversion.convertFromNormalizedParameterValue:
function convertFromNormalizedParameterValue(index, value) {
    value = (value < 0 ? 0 : (value > 1 ? 1 : value));

    switch (index) {
    case 3:
        {
            value = (value < 0 ? 0 : (value > 1 ? 1 : value));

            {
                return 1 + value * (50 - 1);
            }
        }
    case 0:
        {
            value = (value < 0 ? 0 : (value > 1 ? 1 : value));

            {
                return 1 + value * (500 - 1);
            }
        }
    case 1:
        {
            value = (value < 0 ? 0 : (value > 1 ? 1 : value));

            {
                return 10 + value * (500 - 10);
            }
        }
    case 2:
        {
            value = (value < 0 ? 0 : (value > 1 ? 1 : value));

            {
                return -12 + value * (12 - -12);
            }
        }
    default:
        return value;
    }
}

paramConversion.constrainParameterValue:
function constrainParameterValue(index, value) {
    var v = value;

    switch (index) {
    case 0:
        {
            v = (v > 500 ? 500 : (v < 1 ? 1 : v));
            return v;
        }
    case 1:
        {
            v = (v > 500 ? 500 : (v < 10 ? 10 : v));
            return v;
        }
    case 2:
        {
            v = (v > 12 ? 12 : (v < -12 ? -12 : v));
            return v;
        }
    case 3:
        {
            v = (v > 50 ? 50 : (v < 1 ? 1 : v));
            return v;
        }
    default:
        return value;
    }
}
